mapper.select:
select id, name, email, phoneNumber, password, user_role, create_time
from user
where id = #{id,jdbcType=INTEGER}

select id, name, email, phoneNumber, password, user_role, create_time
from user
where email = #{email,jdbcType=VARCHAR}

select id, name, email, phoneNumber, password, user_role, create_time
from user
where phoneNumber = #{phonenumber,jdbcType=VARCHAR}
